    &lt;div class=&quot;blob-content&quot;&gt;
        &lt;div class=&quot;image-right&quot; style=&quot;width:225px&quot;&gt;
            &lt;img class=&quot;img-link&quot; alt=&quot;U.S. Army Specialist Sarah Beckstrom and U.S. Air Force Staff Sergeant Andrew Wolfe&quot; loading=&quot;lazy&quot; decoding=&quot;async&quot; style=&quot;--img-width:200&quot; src=&quot;/images/472o3rLLCA-200.webp&quot; width=&quot;200&quot; height=&quot;110&quot;&gt;
