          &lt;img class=&quot;img-link&quot; alt=&quot;U.S. Customs and Border Protection seal&quot; loading=&quot;lazy&quot; decoding=&quot;async&quot; src=&quot;/images/Zg8vygBL09-200.webp&quot; width=&quot;200&quot; height=&quot;200&quot;&gt;
